"use strict";

var sandwichMenu = function sandwichMenu() {
  var openBtn = document.querySelector('.sandwich__open-btn');
  var closeBtn = document.querySelector('.sandwich__close-btn');
  var content = document.querySelector('.sandwich__content');
  openBtn.addEventListener('click', function (evt) {
    content.classList.add('sandwich__content--opened');
  });
  closeBtn.addEventListener('click', function (evt) {
    content.classList.remove('sandwich__content--opened');
  });
};

var dropdown = function dropdown() {
  var item = document.querySelector('.main-nav__item');
  var list = document.querySelector('.dropdown-list');
  item.addEventListener('click', function (evt) {
    list.classList.toggle('dropdown-list--opened');
    item.classList.toggle('main-nav__item--active');
  });
};

